/*global chrome*/
import { useState } from 'react';
import './App.css';

function App() {
  const [isOpen, setIsOpen] = useState(false);

  const openSidebar = () => {
  //look for the tab that's active and the current window
    chrome.tabs.query({active: true, currentWindow: true}, (tabs) => {
      // const activeTabId = tabs[0].id
      // chrome.scripting.executeScript ({
      //   target: {tabId: activeTabId},
      //   function: () => console.log("tab id: " + {activeTabId}), 
      // });
      chrome.sidebarAction.open();
      console.log(isOpen);
      setIsOpen(true);
    });
  };

  return (
    <div onLoad={openSidebar()}>
      {isOpen && 
        <div>
          <p>hello world!</p>
        </div>
      }
    </div>
  )

}

export default App;
